/**
 * CRUD hooks for User
 * Auto-generated by crud-scaffolder module
 *
 * These hooks call API routes (not Supabase directly) since they run client-side.
 */

"use client";

import { useState, useEffect, useCallback, useRef } from "react";
import type { User, UserCreate, UserUpdate } from "@/lib/schemas/user";

interface Pagination {
  page: number;
  limit: number;
  total: number;
  totalPages: number;
}

interface UseUsersOptions {
  page?: number;
  limit?: number;
  sort?: string;
  order?: "asc" | "desc";
  search?: string;
  enabled?: boolean;
}

interface UseUsersResult {
  data: User[];
  pagination: Pagination | null;
  isLoading: boolean;
  error: string | null;
  refetch: () => Promise<void>;
  create: (input: UserCreate) => Promise<User | null>;
  update: (id: string, input: UserUpdate) => Promise<User | null>;
  remove: (id: string) => Promise<boolean>;
}

export function useUsers(options: UseUsersOptions = {}): UseUsersResult {
  const { page = 1, limit = 20, sort = "created_at", order = "desc", search, enabled = true } = options;
  const [data, setData] = useState<User[]>([]);
  const [pagination, setPagination] = useState<Pagination | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const abortRef = useRef<AbortController | null>(null);

  const fetchData = useCallback(async () => {
    if (!enabled) return;

    // Cancel any in-flight request
    abortRef.current?.abort();
    const controller = new AbortController();
    abortRef.current = controller;

    setIsLoading(true);
    setError(null);
    try {
      const params = new URLSearchParams({
        page: String(page),
        limit: String(limit),
        sort,
        order,
      });
      if (search) params.set("search", search);

      const res = await fetch(`/api/users?${params}`, {
        signal: controller.signal,
      });
      if (!res.ok) {
        const body = await res.json();
        throw new Error(body.error?.message || "Failed to fetch users");
      }
      const json = await res.json();
      setData(json.data);
      setPagination(json.pagination);
    } catch (e) {
      if (e instanceof DOMException && e.name === "AbortError") return;
      setError(e instanceof Error ? e.message : "Unknown error");
    } finally {
      setIsLoading(false);
    }
  }, [page, limit, sort, order, search, enabled]);

  useEffect(() => {
    fetchData();
    return () => { abortRef.current?.abort(); };
  }, [fetchData]);

  const create = useCallback(async (input: UserCreate): Promise<User | null> => {
    try {
      const res = await fetch("/api/users", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(input),
      });
      if (!res.ok) {
        const body = await res.json();
        throw new Error(body.error?.message || "Failed to create user");
      }
      const json = await res.json();
      await fetchData();
      return json.data as User;
    } catch (e) {
      setError(e instanceof Error ? e.message : "Unknown error");
      return null;
    }
  }, [fetchData]);

  const update = useCallback(async (id: string, input: UserUpdate): Promise<User | null> => {
    try {
      const res = await fetch(`/api/users/${id}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(input),
      });
      if (!res.ok) {
        const body = await res.json();
        throw new Error(body.error?.message || "Failed to update user");
      }
      const json = await res.json();
      await fetchData();
      return json.data as User;
    } catch (e) {
      setError(e instanceof Error ? e.message : "Unknown error");
      return null;
    }
  }, [fetchData]);

  const remove = useCallback(async (id: string): Promise<boolean> => {
    try {
      const res = await fetch(`/api/users/${id}`, { method: "DELETE" });
      if (!res.ok) {
        const body = await res.json();
        throw new Error(body.error?.message || "Failed to delete user");
      }
      await fetchData();
      return true;
    } catch (e) {
      setError(e instanceof Error ? e.message : "Unknown error");
      return false;
    }
  }, [fetchData]);

  return { data, pagination, isLoading, error, refetch: fetchData, create, update, remove };
}

/** Fetch a single User by ID */
export function useUser(id: string | null) {
  const [data, setData] = useState<User | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const fetchData = useCallback(async () => {
    if (!id) return;
    setIsLoading(true);
    setError(null);
    try {
      const res = await fetch(`/api/users/${id}`);
      if (!res.ok) {
        const body = await res.json();
        throw new Error(body.error?.message || "User not found");
      }
      const json = await res.json();
      setData(json.data as User);
    } catch (e) {
      setError(e instanceof Error ? e.message : "Unknown error");
    } finally {
      setIsLoading(false);
    }
  }, [id]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  return { data, isLoading, error, refetch: fetchData };
}
